import React, { useState } from "react";
import Chessboard from "react-chessboard";
import Chess from "chess.js";

function App() {
  const [game, setGame] = useState(new Chess());
  const [history, setHistory] = useState([]);

  // Clone safely
  const safeGameMutate = (modify) => {
    const updated = new Chess(game.fen());
    modify(updated);
    setGame(updated);
  };

  // Handle human move
  const onDrop = async (sourceSquare, targetSquare) => {
    let move = null;
    safeGameMutate((g) => {
      move = g.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: "q",
      });
    });
    if (move === null) return false;

    setHistory((prev) => [...prev, `You: ${move.san}`]);

    // Request AI move
    const response = await fetch("/api/move", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ fen: game.fen() }),
    });
    const data = await response.json();
    const aiMove = data.move;

    safeGameMutate((g) => {
      g.move(aiMove);
    });
    setHistory((prev) => [...prev, `AI: ${aiMove}`]);

    return true;
  };

  // Start new game
  const newGame = () => {
    setGame(new Chess());
    setHistory([]);
  };

  return (
    <div style={{ display: "flex", height: "100vh", background: "#2e7d32", color: "white", fontFamily: "Arial, sans-serif" }}>
      {/* Chessboard */}
      <div style={{ flex: 3, display: "flex", justifyContent: "center", alignItems: "center" }}>
        <Chessboard
          position={game.fen()}
          onPieceDrop={onDrop}
          boardWidth={600}
        />
      </div>

      {/* Sidebar */}
      <div style={{ flex: 1, padding: "20px", background: "#1b5e20", overflowY: "auto" }}>
        <h2>Move History</h2>
        <ol>
          {history.map((h, idx) => (
            <li key={idx}>{h}</li>
          ))}
        </ol>
        <button
          onClick={newGame}
          style={{
            marginTop: "20px",
            padding: "10px 20px",
            background: "#66bb6a",
            border: "none",
            borderRadius: "5px",
            color: "white",
            fontWeight: "bold",
            cursor: "pointer",
          }}
        >
          New Game
        </button>
      </div>
    </div>
  );
}

export default App;
